using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataFuncConfig{		/**		* 编号		*/		public int Id;		/**		* 功能ID		*/		public int FuncId;		/**		* 参数1		*/		public int Param1;		/**		* 参数2		*/		public int Param2;		/**		* 参数3		*/		public int Param3;		/**		* 参数4		*/		public int Param4;		/**		* 参数5		*/		public int Param5;		/**		* 参数6		*/		public int Param6;		/**		* 参数7		*/		public int Param7;		/**		* 参数8		*/		public int Param8;		/**		* 参数9		*/		public int Param9;		/**		* 参数10		*/		public int Param10;		/**		* 参数11		*/		public int Param11;		/**		* 参数12		*/		public int Param12;		/**		* 说明		*/		public string Desc;		static public Dictionary<int, TbDataFuncConfig> temples=new Dictionary<int,TbDataFuncConfig>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataFuncConfig tp=new TbDataFuncConfig();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.FuncId=(int)tb["FuncId"];				tp.Param1=(int)tb["Param1"];				tp.Param2=(int)tb["Param2"];				tp.Param3=(int)tb["Param3"];				tp.Param4=(int)tb["Param4"];				tp.Param5=(int)tb["Param5"];				tp.Param6=(int)tb["Param6"];				tp.Param7=(int)tb["Param7"];				tp.Param8=(int)tb["Param8"];				tp.Param9=(int)tb["Param9"];				tp.Param10=(int)tb["Param10"];				tp.Param11=(int)tb["Param11"];				tp.Param12=(int)tb["Param12"];				tp.Desc=(string)tb["Desc"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataFuncConfig select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}