using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataFuncOpen{		/**		* 功能ID		*/		public int Id;		/**		* 功能名称		*/		public string Name;		/**		* 是否显示推送		*/		public bool Push;		/**		* 需求转生等级		*/		public int ReLevel;		/**		* 需求等级		*/		public int Level;		/**		* 显示等级		*/		public int ShowLevel;		/**		* 需求任务ID		*/		public int TaskID;		/**		* 排序		*/		public int Sort;		/**		* 功能图标		*/		public string Icon;		/**		* 是否显示飞行特效		*/		public bool ShowFlyEffect;		/**		* 绑定UI名称		*/		public string BoundName;		/**		* 飞行特效UI名称		*/		public string FlyEffectBoundName;		/**		* 绑定面板		*/		public int BoundDialog;		/**		* 功能开启后执行客户端功能ID		*/		public int ClientFunctionId;		/**		* 功能开启后客户端功能参数		*/		public string[] ClientFunctionParams;		/**		* 显示说明		*/		public string Desc;		static public Dictionary<int, TbDataFuncOpen> temples=new Dictionary<int,TbDataFuncOpen>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataFuncOpen tp=new TbDataFuncOpen();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Name=(string)tb["Name"];				tp.Push=(bool)tb["Push"];				tp.ReLevel=(int)tb["ReLevel"];				tp.Level=(int)tb["Level"];				tp.ShowLevel=(int)tb["ShowLevel"];				tp.TaskID=(int)tb["TaskID"];				tp.Sort=(int)tb["Sort"];				tp.Icon=(string)tb["Icon"];				tp.ShowFlyEffect=(bool)tb["ShowFlyEffect"];				tp.BoundName=(string)tb["BoundName"];				tp.FlyEffectBoundName=(string)tb["FlyEffectBoundName"];				tp.BoundDialog=(int)tb["BoundDialog"];				tp.ClientFunctionId=(int)tb["ClientFunctionId"];				tp.ClientFunctionParams=(string[])tb["ClientFunctionParams"];				tp.Desc=(string)tb["Desc"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataFuncOpen select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}