using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataGuanJie{		/**		* ID		*/		public int Id;		/**		* 等级		*/		public int Level;		/**		* 升级所需数值		*/		public int Need;		/**		* 最小物攻		*/		public int MinATK;		/**		* 最大物攻		*/		public int MaxATK;		/**		* 最小法攻		*/		public int MinMATK;		/**		* 最大法攻		*/		public int MaxMATK;		/**		* 最小道攻		*/		public int MinSATK;		/**		* 最大道攻		*/		public int MaxSATK;		/**		* 免疫物理伤害		*/		public int DTRWarrior;		/**		* 免疫魔法伤害		*/		public int DTRMage;		/**		* 名称		*/		public string NameZhan;		/**		* 名称		*/		public string NameFa;		/**		* 名称		*/		public string NameDao;		/**		* 名称颜色		*/		public string NameColor;		/**		* buff图标		*/		public string BuffIcon;		static public Dictionary<int, TbDataGuanJie> temples=new Dictionary<int,TbDataGuanJie>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataGuanJie tp=new TbDataGuanJie();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Level=(int)tb["Level"];				tp.Need=(int)tb["Need"];				tp.MinATK=(int)tb["MinATK"];				tp.MaxATK=(int)tb["MaxATK"];				tp.MinMATK=(int)tb["MinMATK"];				tp.MaxMATK=(int)tb["MaxMATK"];				tp.MinSATK=(int)tb["MinSATK"];				tp.MaxSATK=(int)tb["MaxSATK"];				tp.DTRWarrior=(int)tb["DTRWarrior"];				tp.DTRMage=(int)tb["DTRMage"];				tp.NameZhan=(string)tb["NameZhan"];				tp.NameFa=(string)tb["NameFa"];				tp.NameDao=(string)tb["NameDao"];				tp.NameColor=(string)tb["NameColor"];				tp.BuffIcon=(string)tb["BuffIcon"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataGuanJie select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}