using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataGuildHegemony{		/**		* id		*/		public int Id;		/**		* 帮主奖励		*/		public int Money1;		/**		* 副帮主奖励		*/		public int Money2;		/**		* 长老奖励		*/		public int Money3;		/**		* 成员奖励		*/		public int Money4;		static public Dictionary<int, TbDataGuildHegemony> temples=new Dictionary<int,TbDataGuildHegemony>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataGuildHegemony tp=new TbDataGuildHegemony();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Money1=(int)tb["Money1"];				tp.Money2=(int)tb["Money2"];				tp.Money3=(int)tb["Money3"];				tp.Money4=(int)tb["Money4"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataGuildHegemony select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}