using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataGuildLevel{		/**		* ID		*/		public int Id;		/**		* 等级		*/		public int Level;		/**		* 升级所需帮会资金		*/		public int SocietyMoney;		/**		* 最大人数		*/		public int Num;		/**		* 最小物攻		*/		public int MinATK;		/**		* 最大物攻		*/		public int MaxATK;		/**		* 最小法攻		*/		public int MinMATK;		/**		* 最大法攻		*/		public int MaxMATK;		/**		* 最小道攻		*/		public int MinSATK;		/**		* 最大道攻		*/		public int MaxSATK;		/**		* buff图标		*/		public string BuffIcon;		/**		* 描述		*/		public string Desc;		static public Dictionary<int, TbDataGuildLevel> temples=new Dictionary<int,TbDataGuildLevel>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataGuildLevel tp=new TbDataGuildLevel();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Level=(int)tb["Level"];				tp.SocietyMoney=(int)tb["SocietyMoney"];				tp.Num=(int)tb["Num"];				tp.MinATK=(int)tb["MinATK"];				tp.MaxATK=(int)tb["MaxATK"];				tp.MinMATK=(int)tb["MinMATK"];				tp.MaxMATK=(int)tb["MaxMATK"];				tp.MinSATK=(int)tb["MinSATK"];				tp.MaxSATK=(int)tb["MaxSATK"];				tp.BuffIcon=(string)tb["BuffIcon"];				tp.Desc=(string)tb["Desc"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataGuildLevel select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}