using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataGuildPost{		/**		* ID		*/		public int Id;		/**		* 名称		*/		public string Name;		/**		* 该职位人数限制		*/		public int MaxNum;		/**		* 可任命最高职位		*/		public int MaxJOB;		/**		* 可否收会员		*/		public int CanIn;		/**		* 可否开除成员		*/		public int CanOut;		/**		* 是否可摧毁仓库物品		*/		public int CanDistroy;		/**		* 可否升级帮会		*/		public int CanUpLevel;		/**		* 是否可解散帮会		*/		public int CanDismiss;		/**		* 是否可编辑帮会公告		*/		public int CanChangeNotice;		static public Dictionary<int, TbDataGuildPost> temples=new Dictionary<int,TbDataGuildPost>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataGuildPost tp=new TbDataGuildPost();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Name=(string)tb["Name"];				tp.MaxNum=(int)tb["MaxNum"];				tp.MaxJOB=(int)tb["MaxJOB"];				tp.CanIn=(int)tb["CanIn"];				tp.CanOut=(int)tb["CanOut"];				tp.CanDistroy=(int)tb["CanDistroy"];				tp.CanUpLevel=(int)tb["CanUpLevel"];				tp.CanDismiss=(int)tb["CanDismiss"];				tp.CanChangeNotice=(int)tb["CanChangeNotice"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataGuildPost select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}