using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataHolidayActivityConfig{		/**		* 活动类型		*/		public int Id;		/**		* 活动名称		*/		public string Name;		/**		* 是否开启此活动		*/		public bool IsOpen;		/**		* 开启时间（开启特殊活动后，哪几天开启）		*/		public int[] OpenDay;		/**		* 显示界面开始的时间（开启特殊活动天数）		*/		public int ShowStartDay;		/**		* 显示界面结束的时间（开启特殊活动天数）		*/		public int ShowEndDay;		/**		* 时间显示类型		*/		public int ShowTimeType;		/**		* 排序		*/		public int SortNum;		/**		* 客户端功能ID		*/		public int ClientFunctionId;		/**		* 客户端功能参数		*/		public string[] ClientFunctionParams;		/**		* 需要等级		*/		public int NeedLevel;		static public Dictionary<int, TbDataHolidayActivityConfig> temples=new Dictionary<int,TbDataHolidayActivityConfig>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataHolidayActivityConfig tp=new TbDataHolidayActivityConfig();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Name=(string)tb["Name"];				tp.IsOpen=(bool)tb["IsOpen"];				tp.OpenDay=(int[])tb["OpenDay"];				tp.ShowStartDay=(int)tb["ShowStartDay"];				tp.ShowEndDay=(int)tb["ShowEndDay"];				tp.ShowTimeType=(int)tb["ShowTimeType"];				tp.SortNum=(int)tb["SortNum"];				tp.ClientFunctionId=(int)tb["ClientFunctionId"];				tp.ClientFunctionParams=(string[])tb["ClientFunctionParams"];				tp.NeedLevel=(int)tb["NeedLevel"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataHolidayActivityConfig select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}