using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataHolidayActivityTimeConfig{		/**		* 编号		*/		public int Id;		/**		* 年		*/		public int Year;		/**		* 月		*/		public int Month;		/**		* 日		*/		public int Day;		static public Dictionary<int, TbDataHolidayActivityTimeConfig> temples=new Dictionary<int,TbDataHolidayActivityTimeConfig>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataHolidayActivityTimeConfig tp=new TbDataHolidayActivityTimeConfig();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Year=(int)tb["Year"];				tp.Month=(int)tb["Month"];				tp.Day=(int)tb["Day"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataHolidayActivityTimeConfig select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}