using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataIntegral{		/**		* ID		*/		public int Id;		/**		* 职业		*/		public int JOB;		/**		* 类别		*/		public int Type;		/**		* 兑换物品ID		*/		public int ItemID;		/**		* 所需物品ID		*/		public int NeedItemID;		/**		* 所需积分数		*/		public int Needcon;		/**		* 所需转生等级		*/		public int NeedRELev;		static public Dictionary<int, TbDataIntegral> temples=new Dictionary<int,TbDataIntegral>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataIntegral tp=new TbDataIntegral();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.JOB=(int)tb["JOB"];				tp.Type=(int)tb["Type"];				tp.ItemID=(int)tb["ItemID"];				tp.NeedItemID=(int)tb["NeedItemID"];				tp.Needcon=(int)tb["Needcon"];				tp.NeedRELev=(int)tb["NeedRELev"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataIntegral select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}