using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataInvest{		/**		* ID		*/		public int Id;		/**		* 类型		*/		public int Type;		/**		* 条件参数1		*/		public int Condition1;		/**		* 条件参数2		*/		public int Condition2;		/**		* 返还绑元		*/		public int ReturnBindMoney;		static public Dictionary<int, TbDataInvest> temples=new Dictionary<int,TbDataInvest>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataInvest tp=new TbDataInvest();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Type=(int)tb["Type"];				tp.Condition1=(int)tb["Condition1"];				tp.Condition2=(int)tb["Condition2"];				tp.ReturnBindMoney=(int)tb["ReturnBindMoney"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataInvest select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}