using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataItemLimitNumber{		/**		* 物品		*/		public int Id;		/**		* ID		*/		public int Numid;		/**		* 次数		*/		public int Num;		static public Dictionary<int, TbDataItemLimitNumber> temples=new Dictionary<int,TbDataItemLimitNumber>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataItemLimitNumber tp=new TbDataItemLimitNumber();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Numid=(int)tb["Numid"];				tp.Num=(int)tb["Num"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataItemLimitNumber select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}