using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataLegendaryGift{		/**		* id		*/		public int Id;		/**		* 最低排名		*/		public int MinRanking;		/**		* 最高排名		*/		public int MaxRanking;		/**		* 奖励物品		*/		public int[] ItemBaseID;		/**		* 奖励物品		*/		public int[] ItemCount;		static public Dictionary<int, TbDataLegendaryGift> temples=new Dictionary<int,TbDataLegendaryGift>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataLegendaryGift tp=new TbDataLegendaryGift();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.MinRanking=(int)tb["MinRanking"];				tp.MaxRanking=(int)tb["MaxRanking"];				tp.ItemBaseID=(int[])tb["ItemBaseID"];				tp.ItemCount=(int[])tb["ItemCount"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataLegendaryGift select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}