using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataLevelAward{		/**		* 人物等级		*/		public int Id;		/**		* 道具ID		*/		public int[] ItemID;		/**		* 道具个数		*/		public int[] ItemCount;		static public Dictionary<int, TbDataLevelAward> temples=new Dictionary<int,TbDataLevelAward>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataLevelAward tp=new TbDataLevelAward();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ItemID=(int[])tb["ItemID"];				tp.ItemCount=(int[])tb["ItemCount"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataLevelAward select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}