using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataMallBase{		/**		* ID		*/		public int Id;		/**		* 排序		*/		public int Position;		/**		* 商店类别		*/		public int Type;		/**		* 物品ID		*/		public int ItembaseID;		/**		* 物品数目		*/		public int ItemNum;		/**		* 需要货币		*/		public int MoneyKind;		/**		* 货币数目		*/		public int Money;		/**		* 绑定状态		*/		public int IsBindKind;		/**		* 最大vip等级限制		*/		public int MaxViplevel;		/**		* 打折前价格		*/		public int StartMoney;		/**		* 每日最大购买数量		*/		public int MaxCount;		/**		* 开始卖天数		*/		public int Time;		/**		* 结束卖天数		*/		public int EndTime;		/**		* 快速购买位值类型		*/		public int QuickBuyType;		/**		* 快速购买排序		*/		public int QuickBuyNum;		static public Dictionary<int, TbDataMallBase> temples=new Dictionary<int,TbDataMallBase>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataMallBase tp=new TbDataMallBase();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Position=(int)tb["Position"];				tp.Type=(int)tb["Type"];				tp.ItembaseID=(int)tb["ItembaseID"];				tp.ItemNum=(int)tb["ItemNum"];				tp.MoneyKind=(int)tb["MoneyKind"];				tp.Money=(int)tb["Money"];				tp.IsBindKind=(int)tb["IsBindKind"];				tp.MaxViplevel=(int)tb["MaxViplevel"];				tp.StartMoney=(int)tb["StartMoney"];				tp.MaxCount=(int)tb["MaxCount"];				tp.Time=(int)tb["Time"];				tp.EndTime=(int)tb["EndTime"];				tp.QuickBuyType=(int)tb["QuickBuyType"];				tp.QuickBuyNum=(int)tb["QuickBuyNum"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataMallBase select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}