using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataMapTransfer{		/**		* 编号		*/		public int Id;		/**		* 传送点名称		*/		public string Name;		/**		* 传送点名称		*/		public string Desc1;		/**		* 所属地图		*/		public string Desc2;		/**		* 是否显示在当前地图上		*/		public bool IsShow;		/**		* 是否显示传送按钮		*/		public bool IsShowTransfer;		/**		* 传送点类型		*/		public int Type;		/**		* 传送点所在地图编号		*/		public int MapId;		/**		* 传送点X坐标		*/		public int MapX;		/**		* 传送点Y坐标		*/		public int MapY;		/**		* 传送点偏移量		*/		public int Offset;		/**		* 传送点等级		*/		public int Level;		static public Dictionary<int, TbDataMapTransfer> temples=new Dictionary<int,TbDataMapTransfer>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataMapTransfer tp=new TbDataMapTransfer();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Name=(string)tb["Name"];				tp.Desc1=(string)tb["Desc1"];				tp.Desc2=(string)tb["Desc2"];				tp.IsShow=(bool)tb["IsShow"];				tp.IsShowTransfer=(bool)tb["IsShowTransfer"];				tp.Type=(int)tb["Type"];				tp.MapId=(int)tb["MapId"];				tp.MapX=(int)tb["MapX"];				tp.MapY=(int)tb["MapY"];				tp.Offset=(int)tb["Offset"];				tp.Level=(int)tb["Level"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataMapTransfer select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}