using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataMars{		/**		* ID		*/		public int Id;		/**		* 排序		*/		public int SortNum;		/**		* 默认是否显示		*/		public int CanShow;		/**		* 激活所需登录天数		*/		public int NeedDay;		/**		* 激活所需vip		*/		public int NeedVIP;		/**		* 激活所需物品		*/		public int NeedItemID;		/**		* 激活所需绑元		*/		public int NeedBindMoney;		/**		* 激活所需元宝		*/		public int NeedMoney;		/**		* 激活所需护卫ID		*/		public int NeedID;		/**		* 激活时间		*/		public int Time;		/**		* 激活玩家buff		*/		public int BuffID;		/**		* 对应单位ID		*/		public int UnitID;		/**		* 最小物攻		*/		public int MinATK;		/**		* 最大物攻		*/		public int MaxATK;		/**		* 最小法攻		*/		public int MinMATK;		/**		* 最大法攻		*/		public int MaxMATK;		/**		* 最小物防		*/		public int MinDEF;		/**		* 最大物防		*/		public int MaxDEF;		/**		* 最小法防		*/		public int MinMDEF;		/**		* 最大法防		*/		public int MaxMDEF;		/**		* 血量		*/		public int HP;		/**		* 回血速度		*/		public int HPUPSpeed;		/**		* 技能ID		*/		public int SkillID;		/**		* 武器ID		*/		public int WeaponID;		/**		* 衣服ID		*/		public int ClothesID;		/**		* 部件1		*/		public int ID1;		/**		* 部件2		*/		public int ID2;		/**		* 部件3		*/		public int ID3;		/**		* 默认对话		*/		public string Dialog;		/**		* 护卫介绍		*/		public string Des;		/**		* 护卫Tips		*/		public string Tips;		/**		* 护卫名字		*/		public string Name;		static public Dictionary<int, TbDataMars> temples=new Dictionary<int,TbDataMars>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataMars tp=new TbDataMars();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.SortNum=(int)tb["SortNum"];				tp.CanShow=(int)tb["CanShow"];				tp.NeedDay=(int)tb["NeedDay"];				tp.NeedVIP=(int)tb["NeedVIP"];				tp.NeedItemID=(int)tb["NeedItemID"];				tp.NeedBindMoney=(int)tb["NeedBindMoney"];				tp.NeedMoney=(int)tb["NeedMoney"];				tp.NeedID=(int)tb["NeedID"];				tp.Time=(int)tb["Time"];				tp.BuffID=(int)tb["BuffID"];				tp.UnitID=(int)tb["UnitID"];				tp.MinATK=(int)tb["MinATK"];				tp.MaxATK=(int)tb["MaxATK"];				tp.MinMATK=(int)tb["MinMATK"];				tp.MaxMATK=(int)tb["MaxMATK"];				tp.MinDEF=(int)tb["MinDEF"];				tp.MaxDEF=(int)tb["MaxDEF"];				tp.MinMDEF=(int)tb["MinMDEF"];				tp.MaxMDEF=(int)tb["MaxMDEF"];				tp.HP=(int)tb["HP"];				tp.HPUPSpeed=(int)tb["HPUPSpeed"];				tp.SkillID=(int)tb["SkillID"];				tp.WeaponID=(int)tb["WeaponID"];				tp.ClothesID=(int)tb["ClothesID"];				tp.ID1=(int)tb["ID1"];				tp.ID2=(int)tb["ID2"];				tp.ID3=(int)tb["ID3"];				tp.Dialog=(string)tb["Dialog"];				tp.Des=(string)tb["Des"];				tp.Tips=(string)tb["Tips"];				tp.Name=(string)tb["Name"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataMars select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}