using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataMineCopybook{		/**		* ID		*/		public int Id;		/**		* 兑换需求		*/		public int[] Need;		/**		* 道具ID		*/		public int ItemID;		/**		* 道具个数		*/		public int ItemCount;		static public Dictionary<int, TbDataMineCopybook> temples=new Dictionary<int,TbDataMineCopybook>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataMineCopybook tp=new TbDataMineCopybook();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Need=(int[])tb["Need"];				tp.ItemID=(int)tb["ItemID"];				tp.ItemCount=(int)tb["ItemCount"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataMineCopybook select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}