using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataMineGetGroup{		/**		* ID		*/		public int Id;		/**		* 类别		*/		public int Type;		/**		* 参数1		*/		public int Param1;		/**		* 参数2		*/		public int Param2;		/**		* 可以的组		*/		public int[] Team;		/**		* 备注		*/		public string Desc;		static public Dictionary<int, TbDataMineGetGroup> temples=new Dictionary<int,TbDataMineGetGroup>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataMineGetGroup tp=new TbDataMineGetGroup();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Type=(int)tb["Type"];				tp.Param1=(int)tb["Param1"];				tp.Param2=(int)tb["Param2"];				tp.Team=(int[])tb["Team"];				tp.Desc=(string)tb["Desc"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataMineGetGroup select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}