using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataMineItem{		/**		* id		*/		public int Id;		/**		* 组别		*/		public int Group;		/**		* 物品ID		*/		public int Itemid;		/**		* 权重		*/		public int Weight;		/**		* 是否绑定		*/		public int Isbind;		/**		* 是否是贵重物品		*/		public int Isgood;		/**		* 是否展示		*/		public int Isshow;		/**		* 是否可回收		*/		public int Canremove;		static public Dictionary<int, TbDataMineItem> temples=new Dictionary<int,TbDataMineItem>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataMineItem tp=new TbDataMineItem();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Group=(int)tb["Group"];				tp.Itemid=(int)tb["Itemid"];				tp.Weight=(int)tb["Weight"];				tp.Isbind=(int)tb["Isbind"];				tp.Isgood=(int)tb["Isgood"];				tp.Isshow=(int)tb["Isshow"];				tp.Canremove=(int)tb["Canremove"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataMineItem select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}