using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataMineProb{		/**		* 组		*/		public int Id;		/**		* 出现概率		*/		public int Getnum;		/**		* 开服起始最小值		*/		public int MinStart;		/**		* 开服起始最大值		*/		public int MaxStart;		/**		* 抽取次数		*/		public int StartTimes;		static public Dictionary<int, TbDataMineProb> temples=new Dictionary<int,TbDataMineProb>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataMineProb tp=new TbDataMineProb();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Getnum=(int)tb["Getnum"];				tp.MinStart=(int)tb["MinStart"];				tp.MaxStart=(int)tb["MaxStart"];				tp.StartTimes=(int)tb["StartTimes"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataMineProb select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}