using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataNonsuch{		/**		* ID		*/		public int Id;		/**		* 物品ID		*/		public int ItemID;		/**		* 条数		*/		public int Num;		/**		* 权重值		*/		public int Weight;		static public Dictionary<int, TbDataNonsuch> temples=new Dictionary<int,TbDataNonsuch>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataNonsuch tp=new TbDataNonsuch();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ItemID=(int)tb["ItemID"];				tp.Num=(int)tb["Num"];				tp.Weight=(int)tb["Weight"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataNonsuch select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}