using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataNpcTransfer{		/**		* ID		*/		public int Id;		/**		* 类别		*/		public int Type;		/**		* 地图名		*/		public string Name;		/**		* 传送点ID		*/		public int TeleportId;		/**		* 消耗金币		*/		public int Needgolds;		/**		* 所需等级		*/		public int NeedLevel;		/**		* 所需转生等级		*/		public int NeedRELevel;		/**		* 所需VIP		*/		public int NeedVipLevel;		static public Dictionary<int, TbDataNpcTransfer> temples=new Dictionary<int,TbDataNpcTransfer>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataNpcTransfer tp=new TbDataNpcTransfer();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Type=(int)tb["Type"];				tp.Name=(string)tb["Name"];				tp.TeleportId=(int)tb["TeleportId"];				tp.Needgolds=(int)tb["Needgolds"];				tp.NeedLevel=(int)tb["NeedLevel"];				tp.NeedRELevel=(int)tb["NeedRELevel"];				tp.NeedVipLevel=(int)tb["NeedVipLevel"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataNpcTransfer select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}