using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataOfficer{		/**		* ID		*/		public int Id;		/**		* 等级		*/		public int Level;		/**		* 升级所需声望		*/		public int NeedPrestige;		/**		* 名称		*/		public string Name;		/**		* 最小物攻		*/		public int MinATK;		/**		* 最大物攻		*/		public int MaxATK;		/**		* 最小法攻		*/		public int MinMATK;		/**		* 最大法攻		*/		public int MaxMATK;		/**		* 最小道攻		*/		public int MinSATK;		/**		* 最大道攻		*/		public int MaxSATK;		/**		* 每日可领取绑金		*/		public int AwardGoldBind;		/**		* 每日可领取礼券		*/		public int AwardMoneyBind;		static public Dictionary<int, TbDataOfficer> temples=new Dictionary<int,TbDataOfficer>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataOfficer tp=new TbDataOfficer();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Level=(int)tb["Level"];				tp.NeedPrestige=(int)tb["NeedPrestige"];				tp.Name=(string)tb["Name"];				tp.MinATK=(int)tb["MinATK"];				tp.MaxATK=(int)tb["MaxATK"];				tp.MinMATK=(int)tb["MinMATK"];				tp.MaxMATK=(int)tb["MaxMATK"];				tp.MinSATK=(int)tb["MinSATK"];				tp.MaxSATK=(int)tb["MaxSATK"];				tp.AwardGoldBind=(int)tb["AwardGoldBind"];				tp.AwardMoneyBind=(int)tb["AwardMoneyBind"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataOfficer select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}