using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataOnlineAward{		/**		* ID		*/		public int Id;		/**		* 所需时间		*/		public int Time;		/**		* 物品ID		*/		public int[] ItemBaseID;		/**		* 物品数量		*/		public int[] ItemCount;		/**		* 描述		*/		public string Desc;		static public Dictionary<int, TbDataOnlineAward> temples=new Dictionary<int,TbDataOnlineAward>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataOnlineAward tp=new TbDataOnlineAward();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Time=(int)tb["Time"];				tp.ItemBaseID=(int[])tb["ItemBaseID"];				tp.ItemCount=(int[])tb["ItemCount"];				tp.Desc=(string)tb["Desc"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataOnlineAward select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}