using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataOnlineAwardHa{		/**		* ID		*/		public int Id;		/**		* 所需时间		*/		public int Time;		/**		* 物品ID		*/		public int[] ItemBaseID;		/**		* 物品数量		*/		public int[] ItemCount;		/**		* 是否绑定		*/		public int[] Isbind;		/**		* 描述		*/		public string Desc;		static public Dictionary<int, TbDataOnlineAwardHa> temples=new Dictionary<int,TbDataOnlineAwardHa>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataOnlineAwardHa tp=new TbDataOnlineAwardHa();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Time=(int)tb["Time"];				tp.ItemBaseID=(int[])tb["ItemBaseID"];				tp.ItemCount=(int[])tb["ItemCount"];				tp.Isbind=(int[])tb["Isbind"];				tp.Desc=(string)tb["Desc"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataOnlineAwardHa select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}