using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataPlatform360Privilege{		/**		* 编号		*/		public int Id;		/**		* 奖励类型		*/		public int AwardType;		/**		* 领取标记		*/		public int Flag;		/**		* 360等级		*/		public int NeedLevel;		/**		* 奖励道具		*/		public int[] ItemBaseID;		/**		* 道具数量		*/		public int[] ItemCount;		/**		* 绑定状态		*/		public int[] ItemBindType;		static public Dictionary<int, TbDataPlatform360Privilege> temples=new Dictionary<int,TbDataPlatform360Privilege>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataPlatform360Privilege tp=new TbDataPlatform360Privilege();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.AwardType=(int)tb["AwardType"];				tp.Flag=(int)tb["Flag"];				tp.NeedLevel=(int)tb["NeedLevel"];				tp.ItemBaseID=(int[])tb["ItemBaseID"];				tp.ItemCount=(int[])tb["ItemCount"];				tp.ItemBindType=(int[])tb["ItemBindType"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataPlatform360Privilege select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}