using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataPrivateBoss{		/**		* 进入次数记录数据枚举		*/		public int Id;		/**		* 单位静态ID		*/		public int BossBaseID;		/**		* 等级限制		*/		public int LevelRe;		/**		* 等级限制		*/		public int Level;		/**		* 可进入次数		*/		public int Count;		/**		* 战士战力值		*/		public int ZhanFightPower;		/**		* 法师战力值		*/		public int FaFightPower;		/**		* 道士战力值		*/		public int DaoFightPower;		/**		* 副本ID		*/		public int SceneID;		/**		* 消耗道具ID		*/		public int ItemID;		/**		* 消耗数量		*/		public int ItemCount;		/**		* 备注		*/		public string Desc;		/**		* 名称等级图标		*/		public string LevelIcon;		/**		* X偏移		*/		public int OffsetX;		/**		* Y偏移		*/		public int OffsetY;		/**		* 缩放		*/		public int Scale;		static public Dictionary<int, TbDataPrivateBoss> temples=new Dictionary<int,TbDataPrivateBoss>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataPrivateBoss tp=new TbDataPrivateBoss();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.BossBaseID=(int)tb["BossBaseID"];				tp.LevelRe=(int)tb["LevelRe"];				tp.Level=(int)tb["Level"];				tp.Count=(int)tb["Count"];				tp.ZhanFightPower=(int)tb["ZhanFightPower"];				tp.FaFightPower=(int)tb["FaFightPower"];				tp.DaoFightPower=(int)tb["DaoFightPower"];				tp.SceneID=(int)tb["SceneID"];				tp.ItemID=(int)tb["ItemID"];				tp.ItemCount=(int)tb["ItemCount"];				tp.Desc=(string)tb["Desc"];				tp.LevelIcon=(string)tb["LevelIcon"];				tp.OffsetX=(int)tb["OffsetX"];				tp.OffsetY=(int)tb["OffsetY"];				tp.Scale=(int)tb["Scale"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataPrivateBoss select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}