using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataProfession{		/**		* ID		*/		public int Id;		/**		* 名称		*/		public string Name;		/**		* 描述		*/		public string Desc;		/**		* 默认技能		*/		public int StartSkill;		/**		* 初始命中		*/		public int HIT;		/**		* 初始闪避		*/		public int DODGE;		/**		* 初始男形象		*/		public int MaleResId;		/**		* 初始女形象		*/		public int FemaleResId;		/**		* 初始男形象		*/		public int MaleRidingResId;		/**		* 初始女形象		*/		public int FemaleRidingResId;		/**		* 默认内观男图片		*/		public string MaleItemInIcon;		/**		* 默认内观女图片		*/		public string FemaleItemInIcon;		/**		* 移动时间		*/		public int MoveTime;		static public Dictionary<int, TbDataProfession> temples=new Dictionary<int,TbDataProfession>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataProfession tp=new TbDataProfession();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Name=(string)tb["Name"];				tp.Desc=(string)tb["Desc"];				tp.StartSkill=(int)tb["StartSkill"];				tp.HIT=(int)tb["HIT"];				tp.DODGE=(int)tb["DODGE"];				tp.MaleResId=(int)tb["MaleResId"];				tp.FemaleResId=(int)tb["FemaleResId"];				tp.MaleRidingResId=(int)tb["MaleRidingResId"];				tp.FemaleRidingResId=(int)tb["FemaleRidingResId"];				tp.MaleItemInIcon=(string)tb["MaleItemInIcon"];				tp.FemaleItemInIcon=(string)tb["FemaleItemInIcon"];				tp.MoveTime=(int)tb["MoveTime"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataProfession select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}