using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataRecastBase{		/**		* ID		*/		public int Id;		/**		* 物品ID		*/		public int ItemID;		/**		* 属性ID		*/		public int BaseID;		/**		* 权重值		*/		public int Weight;		/**		* 属性最小值		*/		public int MinBase;		/**		* 属性最大值		*/		public int MaxBase;		static public Dictionary<int, TbDataRecastBase> temples=new Dictionary<int,TbDataRecastBase>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataRecastBase tp=new TbDataRecastBase();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ItemID=(int)tb["ItemID"];				tp.BaseID=(int)tb["BaseID"];				tp.Weight=(int)tb["Weight"];				tp.MinBase=(int)tb["MinBase"];				tp.MaxBase=(int)tb["MaxBase"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataRecastBase select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}