using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataRecastExpend{		/**		* ID		*/		public int Id;		/**		* 物品ID		*/		public int ItemID;		/**		* 继承属性所需元宝		*/		public int MovedNeedmoney;		/**		* 洗练属性所需物品		*/		public int ChangeKindItemID;		/**		* 洗练属性所需物品数		*/		public int ChangeKindItemNum;		/**		* 洗练值所需物品		*/		public int ChangeBaseItemID;		/**		* 洗练值所需物品数		*/		public int ChangeBaseItemNum;		static public Dictionary<int, TbDataRecastExpend> temples=new Dictionary<int,TbDataRecastExpend>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataRecastExpend tp=new TbDataRecastExpend();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ItemID=(int)tb["ItemID"];				tp.MovedNeedmoney=(int)tb["MovedNeedmoney"];				tp.ChangeKindItemID=(int)tb["ChangeKindItemID"];				tp.ChangeKindItemNum=(int)tb["ChangeKindItemNum"];				tp.ChangeBaseItemID=(int)tb["ChangeBaseItemID"];				tp.ChangeBaseItemNum=(int)tb["ChangeBaseItemNum"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataRecastExpend select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}