using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataRecastLight{		/**		* ID		*/		public int Id;		/**		* 物品ID		*/		public int ItemID;		/**		* 开光条数		*/		public int AddBaseNum;		/**		* 所需物品ID		*/		public int NeedItemID;		/**		* 所需物品数		*/		public int NeedItemNum;		/**		* 继承所需元宝		*/		public int Needmoney;		static public Dictionary<int, TbDataRecastLight> temples=new Dictionary<int,TbDataRecastLight>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataRecastLight tp=new TbDataRecastLight();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ItemID=(int)tb["ItemID"];				tp.AddBaseNum=(int)tb["AddBaseNum"];				tp.NeedItemID=(int)tb["NeedItemID"];				tp.NeedItemNum=(int)tb["NeedItemNum"];				tp.Needmoney=(int)tb["Needmoney"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataRecastLight select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}