using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataRechargeAward{		/**		* id		*/		public int Id;		/**		* 天数		*/		public int Day;		/**		* 充值人数		*/		public int HumanNum;		/**		* 领取等级限制(>=)		*/		public int Level;		/**		* 等级奖励		*/		public int[] LevelItem;		/**		* 等级奖励物品数		*/		public int[] LevelItemNum;		/**		* 任意金额限制(>=)		*/		public int Recharge;		/**		* 任意充值奖励		*/		public int[] FreeItem;		/**		* 任意充值奖励物品数		*/		public int[] FreeItemNum;		/**		* 累计充值金额(>=)		*/		public int Total;		/**		* 充值奖励物品		*/		public int[] MoneyItem;		/**		* 充值奖励物品数		*/		public int[] MoneyItemNum;		static public Dictionary<int, TbDataRechargeAward> temples=new Dictionary<int,TbDataRechargeAward>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataRechargeAward tp=new TbDataRechargeAward();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Day=(int)tb["Day"];				tp.HumanNum=(int)tb["HumanNum"];				tp.Level=(int)tb["Level"];				tp.LevelItem=(int[])tb["LevelItem"];				tp.LevelItemNum=(int[])tb["LevelItemNum"];				tp.Recharge=(int)tb["Recharge"];				tp.FreeItem=(int[])tb["FreeItem"];				tp.FreeItemNum=(int[])tb["FreeItemNum"];				tp.Total=(int)tb["Total"];				tp.MoneyItem=(int[])tb["MoneyItem"];				tp.MoneyItemNum=(int[])tb["MoneyItemNum"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataRechargeAward select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}