using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataRoleInitAttr{		/**		* ID		*/		public int Id;		/**		* 属性枚举		*/		public int AttrType;		/**		* 属性数值		*/		public int AttrValue;		/**		* 备注		*/		public string Desc;		static public Dictionary<int, TbDataRoleInitAttr> temples=new Dictionary<int,TbDataRoleInitAttr>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataRoleInitAttr tp=new TbDataRoleInitAttr();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.AttrType=(int)tb["AttrType"];				tp.AttrValue=(int)tb["AttrValue"];				tp.Desc=(string)tb["Desc"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataRoleInitAttr select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}