using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataRoleInsidePower{		/**		* 编号		*/		public int Id;		/**		* 内功等级		*/		public int Lev;		/**		* 内功经验		*/		public long Exp;		/**		* 战士内功值		*/		public int ValWarrior;		/**		* 法师内功值		*/		public int ValMage;		/**		* 道士内功值		*/		public int ValPriests;		/**		* 吸伤比例		*/		public int Dtr;		/**		* 战士内功恢复值		*/		public int AddvalWarrior;		/**		* 法师内功恢复值		*/		public int AddvalMage;		/**		* 道士内功恢复值		*/		public int AddvalPriests;		/**		* 战士内功恢复值		*/		public int AddvalFightWarrior;		/**		* 法师内功恢复值		*/		public int AddvalFightMage;		/**		* 道士内功恢复值		*/		public int AddvalFightPriests;		static public Dictionary<int, TbDataRoleInsidePower> temples=new Dictionary<int,TbDataRoleInsidePower>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataRoleInsidePower tp=new TbDataRoleInsidePower();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Lev=(int)tb["Lev"];				tp.Exp=(long)tb["Exp"];				tp.ValWarrior=(int)tb["ValWarrior"];				tp.ValMage=(int)tb["ValMage"];				tp.ValPriests=(int)tb["ValPriests"];				tp.Dtr=(int)tb["Dtr"];				tp.AddvalWarrior=(int)tb["AddvalWarrior"];				tp.AddvalMage=(int)tb["AddvalMage"];				tp.AddvalPriests=(int)tb["AddvalPriests"];				tp.AddvalFightWarrior=(int)tb["AddvalFightWarrior"];				tp.AddvalFightMage=(int)tb["AddvalFightMage"];				tp.AddvalFightPriests=(int)tb["AddvalFightPriests"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataRoleInsidePower select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}