using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataRoleStars{		/**		* ID		*/		public int Id;		/**		* 星宿类别		*/		public int Startype;		/**		* 星宿等级		*/		public int StarLev;		/**		* 升级需要星宿经验		*/		public int StarEXP;		/**		* 最小物攻（战士）		*/		public int MinATKWarrior;		/**		* 最大物攻（战士）		*/		public int MaxATKWarrior;		/**		* 最小法攻（战士）		*/		public int MinMATKMage;		/**		* 最大法攻（战士）		*/		public int MaxMATKMage;		/**		* 最小道攻（战士）		*/		public int MinSATKPriests;		/**		* 最大道攻（战士）		*/		public int MaxSATKPriests;		/**		* 战士内功值		*/		public int InpowerWarrior;		/**		* 法师内功值		*/		public int InpowerMage;		/**		* 道士内功值		*/		public int InpowerPriests;		static public Dictionary<int, TbDataRoleStars> temples=new Dictionary<int,TbDataRoleStars>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataRoleStars tp=new TbDataRoleStars();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Startype=(int)tb["Startype"];				tp.StarLev=(int)tb["StarLev"];				tp.StarEXP=(int)tb["StarEXP"];				tp.MinATKWarrior=(int)tb["MinATKWarrior"];				tp.MaxATKWarrior=(int)tb["MaxATKWarrior"];				tp.MinMATKMage=(int)tb["MinMATKMage"];				tp.MaxMATKMage=(int)tb["MaxMATKMage"];				tp.MinSATKPriests=(int)tb["MinSATKPriests"];				tp.MaxSATKPriests=(int)tb["MaxSATKPriests"];				tp.InpowerWarrior=(int)tb["InpowerWarrior"];				tp.InpowerMage=(int)tb["InpowerMage"];				tp.InpowerPriests=(int)tb["InpowerPriests"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataRoleStars select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}