using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataSecretStoreHa{		/**		* id		*/		public int Id;		/**		* 位置		*/		public int Team;		/**		* 物品ID		*/		public int ItembaseID;		/**		* 物品数目		*/		public int ItemNum;		/**		* 全服限制数目		*/		public int MaxNum;		/**		* 需要货币		*/		public int MoneyKind;		/**		* 货币数目		*/		public int Money;		/**		* 权重		*/		public int Weight;		static public Dictionary<int, TbDataSecretStoreHa> temples=new Dictionary<int,TbDataSecretStoreHa>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataSecretStoreHa tp=new TbDataSecretStoreHa();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Team=(int)tb["Team"];				tp.ItembaseID=(int)tb["ItembaseID"];				tp.ItemNum=(int)tb["ItemNum"];				tp.MaxNum=(int)tb["MaxNum"];				tp.MoneyKind=(int)tb["MoneyKind"];				tp.Money=(int)tb["Money"];				tp.Weight=(int)tb["Weight"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataSecretStoreHa select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}