using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataSevenDays{		/**		* ID		*/		public int Id;		/**		* 战士物品		*/		public int[] ItemID1;		/**		* 战士物品数		*/		public int[] ItemNum1;		/**		* 法师物品		*/		public int[] ItemID2;		/**		* 法师物品数		*/		public int[] ItemNum2;		/**		* 道士物品		*/		public int[] ItemID3;		/**		* 道士物品数		*/		public int[] ItemNum3;		static public Dictionary<int, TbDataSevenDays> temples=new Dictionary<int,TbDataSevenDays>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataSevenDays tp=new TbDataSevenDays();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ItemID1=(int[])tb["ItemID1"];				tp.ItemNum1=(int[])tb["ItemNum1"];				tp.ItemID2=(int[])tb["ItemID2"];				tp.ItemNum2=(int[])tb["ItemNum2"];				tp.ItemID3=(int[])tb["ItemID3"];				tp.ItemNum3=(int[])tb["ItemNum3"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataSevenDays select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}