using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataShaChengAward{		/**		* id		*/		public int Id;		/**		* 物品ID		*/		public int[] ItemBaseID;		/**		* 物品数量		*/		public int[] ItemCount;		/**		* 金币		*/		public int GoldFree;		/**		* 绑金		*/		public int GoldBind;		/**		* 元宝		*/		public int VipPointFree;		/**		* 绑元		*/		public int VipPointBind;		static public Dictionary<int, TbDataShaChengAward> temples=new Dictionary<int,TbDataShaChengAward>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataShaChengAward tp=new TbDataShaChengAward();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ItemBaseID=(int[])tb["ItemBaseID"];				tp.ItemCount=(int[])tb["ItemCount"];				tp.GoldFree=(int)tb["GoldFree"];				tp.GoldBind=(int)tb["GoldBind"];				tp.VipPointFree=(int)tb["VipPointFree"];				tp.VipPointBind=(int)tb["VipPointBind"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataShaChengAward select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}