using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataShaChengZhengBaAward{		/**		* id		*/		public int Id;		/**		* 物品ID		*/		public int[] ItemBaseID;		/**		* 物品数量		*/		public int[] ItemCount;		/**		* 元宝奖励		*/		public int AwardVipPoint;		static public Dictionary<int, TbDataShaChengZhengBaAward> temples=new Dictionary<int,TbDataShaChengZhengBaAward>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataShaChengZhengBaAward tp=new TbDataShaChengZhengBaAward();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ItemBaseID=(int[])tb["ItemBaseID"];				tp.ItemCount=(int[])tb["ItemCount"];				tp.AwardVipPoint=(int)tb["AwardVipPoint"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataShaChengZhengBaAward select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}