using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataSinValue{		/**		* ID		*/		public int Id;		/**		* 所需罪恶值		*/		public int Neednum;		/**		* 背包物品掉落率		*/		public int BagdropRate;		/**		* 不掉装备权重		*/		public int NodropRate;		/**		* 掉落1件装备权重		*/		public int Drop1Rate;		/**		* 掉落2件装备权重		*/		public int Drop2Rate;		/**		* 掉落3件装备权重		*/		public int Drop3Rate;		/**		* 掉落4件装备权重		*/		public int Drop4Rate;		/**		* 颜色		*/		public string Color;		static public Dictionary<int, TbDataSinValue> temples=new Dictionary<int,TbDataSinValue>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataSinValue tp=new TbDataSinValue();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Neednum=(int)tb["Neednum"];				tp.BagdropRate=(int)tb["BagdropRate"];				tp.NodropRate=(int)tb["NodropRate"];				tp.Drop1Rate=(int)tb["Drop1Rate"];				tp.Drop2Rate=(int)tb["Drop2Rate"];				tp.Drop3Rate=(int)tb["Drop3Rate"];				tp.Drop4Rate=(int)tb["Drop4Rate"];				tp.Color=(string)tb["Color"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataSinValue select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}