using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataSkillRatio{		/**		* 技能ID		*/		public int Id;		/**		* 系数1		*/		public int Param1;		/**		* 系数2		*/		public int Param2;		/**		* 系数3		*/		public int Param3;		/**		* 系数4		*/		public int Param4;		/**		* 系数5		*/		public int Param5;		/**		* 系数6		*/		public int Param6;		/**		* 系数7		*/		public int Param7;		/**		* 系数8		*/		public int Param8;		/**		* 系数9		*/		public int Param9;		/**		* 系数10		*/		public int Param10;		static public Dictionary<int, TbDataSkillRatio> temples=new Dictionary<int,TbDataSkillRatio>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataSkillRatio tp=new TbDataSkillRatio();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Param1=(int)tb["Param1"];				tp.Param2=(int)tb["Param2"];				tp.Param3=(int)tb["Param3"];				tp.Param4=(int)tb["Param4"];				tp.Param5=(int)tb["Param5"];				tp.Param6=(int)tb["Param6"];				tp.Param7=(int)tb["Param7"];				tp.Param8=(int)tb["Param8"];				tp.Param9=(int)tb["Param9"];				tp.Param10=(int)tb["Param10"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataSkillRatio select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}