using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataSmelter{		/**		* ID		*/		public int Id;		/**		* 合成物品ID		*/		public int ItemID;		/**		* 主物品ID		*/		public int NeeditemID;		/**		* 合成类型		*/		public int Type;		/**		* 合成等级		*/		public int Level;		/**		* 合成所需属性ID		*/		public int BaseID;		/**		* 合成所需属性值		*/		public int Needbase;		/**		* 合成所需属性ID		*/		public int BaseID2;		/**		* 合成所需属性值		*/		public int Needbase2;		/**		* 合成消耗模式		*/		public int CostMode;		/**		* 所需人物等级		*/		public int HumanLevel;		/**		* 返还物品ID		*/		public int BackitemID;		/**		* 返还属性ID		*/		public int BackbaseID;		/**		* 返还值		*/		public int Backbase;		static public Dictionary<int, TbDataSmelter> temples=new Dictionary<int,TbDataSmelter>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataSmelter tp=new TbDataSmelter();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ItemID=(int)tb["ItemID"];				tp.NeeditemID=(int)tb["NeeditemID"];				tp.Type=(int)tb["Type"];				tp.Level=(int)tb["Level"];				tp.BaseID=(int)tb["BaseID"];				tp.Needbase=(int)tb["Needbase"];				tp.BaseID2=(int)tb["BaseID2"];				tp.Needbase2=(int)tb["Needbase2"];				tp.CostMode=(int)tb["CostMode"];				tp.HumanLevel=(int)tb["HumanLevel"];				tp.BackitemID=(int)tb["BackitemID"];				tp.BackbaseID=(int)tb["BackbaseID"];				tp.Backbase=(int)tb["Backbase"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataSmelter select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}