using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataSoul{		/**		* ID		*/		public int Id;		/**		* 职业		*/		public int JOB;		/**		* 位置		*/		public int Equippos;		/**		* 注灵等级		*/		public int SoulLV;		/**		* 所需注灵数		*/		public int NeedSoul;		/**		* 回收注灵数		*/		public int BackSoul;		/**		* 最小物攻		*/		public int MinATK;		/**		* 最大物攻		*/		public int MaxATK;		/**		* 最小法攻		*/		public int MinMATK;		/**		* 最大法攻		*/		public int MaxMATK;		/**		* 最小道攻		*/		public int MinSATK;		/**		* 最大道攻		*/		public int MaxSATK;		/**		* 最小物防		*/		public int MinDEF;		/**		* 最大物防		*/		public int MaxDEF;		/**		* 最小法防		*/		public int MinMDEF;		/**		* 最大法防		*/		public int MaxMDEF;		static public Dictionary<int, TbDataSoul> temples=new Dictionary<int,TbDataSoul>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataSoul tp=new TbDataSoul();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.JOB=(int)tb["JOB"];				tp.Equippos=(int)tb["Equippos"];				tp.SoulLV=(int)tb["SoulLV"];				tp.NeedSoul=(int)tb["NeedSoul"];				tp.BackSoul=(int)tb["BackSoul"];				tp.MinATK=(int)tb["MinATK"];				tp.MaxATK=(int)tb["MaxATK"];				tp.MinMATK=(int)tb["MinMATK"];				tp.MaxMATK=(int)tb["MaxMATK"];				tp.MinSATK=(int)tb["MinSATK"];				tp.MaxSATK=(int)tb["MaxSATK"];				tp.MinDEF=(int)tb["MinDEF"];				tp.MaxDEF=(int)tb["MaxDEF"];				tp.MinMDEF=(int)tb["MinMDEF"];				tp.MaxMDEF=(int)tb["MaxMDEF"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataSoul select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}