using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataStorage{		/**		* ID		*/		public int Id;		/**		* 格子数		*/		public int Grid;		/**		* 所需元宝		*/		public int NeedMoney;		static public Dictionary<int, TbDataStorage> temples=new Dictionary<int,TbDataStorage>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataStorage tp=new TbDataStorage();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Grid=(int)tb["Grid"];				tp.NeedMoney=(int)tb["NeedMoney"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataStorage select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}