using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataStrengthen{		/**		* ID		*/		public int Id;		/**		* 职业		*/		public int JOB;		/**		* 位置		*/		public int Equippos;		/**		* 强化等级		*/		public int StrLV;		/**		* 成功率		*/		public int SuccessRate;		/**		* 显示百分比		*/		public int SuccessRateDesc;		/**		* 百分百成功所需元宝		*/		public int PercentMoney;		/**		* 强化所需金币		*/		public int StrengthenGold;		/**		* 所需最小强化次数		*/		public int MinTimes;		/**		* 购买次数货币类型		*/		public int BuyTimesKind;		/**		* 购买次数所需金币数		*/		public int BuyTimesMoney;		/**		* 强化转移货币类型		*/		public int TransferKind;		/**		* 强化转移货币数		*/		public int TransferMoney;		/**		* 最小物攻		*/		public int MinATK;		/**		* 最大物攻		*/		public int MaxATK;		/**		* 最小法攻		*/		public int MinMATK;		/**		* 最大法攻		*/		public int MaxMATK;		/**		* 最小道攻		*/		public int MinSATK;		/**		* 最大道攻		*/		public int MaxSATK;		/**		* 最小物防		*/		public int MinDEF;		/**		* 最大物防		*/		public int MaxDEF;		/**		* 最小法防		*/		public int MinMDEF;		/**		* 最大法防		*/		public int MaxMDEF;		static public Dictionary<int, TbDataStrengthen> temples=new Dictionary<int,TbDataStrengthen>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataStrengthen tp=new TbDataStrengthen();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.JOB=(int)tb["JOB"];				tp.Equippos=(int)tb["Equippos"];				tp.StrLV=(int)tb["StrLV"];				tp.SuccessRate=(int)tb["SuccessRate"];				tp.SuccessRateDesc=(int)tb["SuccessRateDesc"];				tp.PercentMoney=(int)tb["PercentMoney"];				tp.StrengthenGold=(int)tb["StrengthenGold"];				tp.MinTimes=(int)tb["MinTimes"];				tp.BuyTimesKind=(int)tb["BuyTimesKind"];				tp.BuyTimesMoney=(int)tb["BuyTimesMoney"];				tp.TransferKind=(int)tb["TransferKind"];				tp.TransferMoney=(int)tb["TransferMoney"];				tp.MinATK=(int)tb["MinATK"];				tp.MaxATK=(int)tb["MaxATK"];				tp.MinMATK=(int)tb["MinMATK"];				tp.MaxMATK=(int)tb["MaxMATK"];				tp.MinSATK=(int)tb["MinSATK"];				tp.MaxSATK=(int)tb["MaxSATK"];				tp.MinDEF=(int)tb["MinDEF"];				tp.MaxDEF=(int)tb["MaxDEF"];				tp.MinMDEF=(int)tb["MinMDEF"];				tp.MaxMDEF=(int)tb["MaxMDEF"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataStrengthen select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}