using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataSuit{		/**		* ID		*/		public int Id;		/**		* 类别		*/		public int Type;		/**		* 镶嵌所需金钱		*/		public int NeedGold;		/**		* 名称		*/		public string Name;		/**		* 所需最小组		*/		public int MinTeam;		/**		* 所需装备数		*/		public int Minnum;		/**		* 战士增加生命值		*/		public int MaxHPWarrior;		/**		* 法师增加生命		*/		public int MaxHPMage;		/**		* 道士增加生命		*/		public int MaxHPPriests;		/**		* 激活技能		*/		public int SkillID;		/**		* 描述		*/		public string Desc;		/**		* 图标		*/		public string HeadIcon;		/**		* 头顶图标		*/		public string InIcon;		/**		* 图标		*/		public string BuffIcon;		/**		* 是否存在套装属性		*/		public int IsSoulTeam;		/**		* 是否存在祈祷技能		*/		public bool Skill1;		/**		* 是否存在记忆技能		*/		public bool Skill2;		static public Dictionary<int, TbDataSuit> temples=new Dictionary<int,TbDataSuit>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataSuit tp=new TbDataSuit();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Type=(int)tb["Type"];				tp.NeedGold=(int)tb["NeedGold"];				tp.Name=(string)tb["Name"];				tp.MinTeam=(int)tb["MinTeam"];				tp.Minnum=(int)tb["Minnum"];				tp.MaxHPWarrior=(int)tb["MaxHPWarrior"];				tp.MaxHPMage=(int)tb["MaxHPMage"];				tp.MaxHPPriests=(int)tb["MaxHPPriests"];				tp.SkillID=(int)tb["SkillID"];				tp.Desc=(string)tb["Desc"];				tp.HeadIcon=(string)tb["HeadIcon"];				tp.InIcon=(string)tb["InIcon"];				tp.BuffIcon=(string)tb["BuffIcon"];				tp.IsSoulTeam=(int)tb["IsSoulTeam"];				tp.Skill1=(bool)tb["Skill1"];				tp.Skill2=(bool)tb["Skill2"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataSuit select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}