using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataTaskDaily{		/**		* ID		*/		public int Id;		/**		* 分组		*/		public int Team;		/**		* 转生等级		*/		public int ReLevel;		/**		* 等级		*/		public int Level;		/**		* 星级		*/		public int Star;		/**		* 出现权重		*/		public int Weight;		/**		* 主任务ID		*/		public int[] TaskID;		static public Dictionary<int, TbDataTaskDaily> temples=new Dictionary<int,TbDataTaskDaily>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataTaskDaily tp=new TbDataTaskDaily();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Team=(int)tb["Team"];				tp.ReLevel=(int)tb["ReLevel"];				tp.Level=(int)tb["Level"];				tp.Star=(int)tb["Star"];				tp.Weight=(int)tb["Weight"];				tp.TaskID=(int[])tb["TaskID"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataTaskDaily select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}