using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataTaskWeekly{		/**		* ID		*/		public int Id;		/**		* 分组		*/		public int Team;		/**		* 转生等级		*/		public int ReLevel;		/**		* 等级		*/		public int Level;		/**		* 最小环数		*/		public int MinRing;		/**		* 最大环数		*/		public int MaxRing;		/**		* 出现权重		*/		public int Weight;		/**		* 主任务ID		*/		public int TaskID;		static public Dictionary<int, TbDataTaskWeekly> temples=new Dictionary<int,TbDataTaskWeekly>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataTaskWeekly tp=new TbDataTaskWeekly();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Team=(int)tb["Team"];				tp.ReLevel=(int)tb["ReLevel"];				tp.Level=(int)tb["Level"];				tp.MinRing=(int)tb["MinRing"];				tp.MaxRing=(int)tb["MaxRing"];				tp.Weight=(int)tb["Weight"];				tp.TaskID=(int)tb["TaskID"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataTaskWeekly select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}