using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataTimeLimit{		/**		* id		*/		public int Id;		/**		* 物品ID		*/		public int ItembaseID;		/**		* 物品数目		*/		public int ItemNum;		/**		* 需要货币		*/		public int MoneyKind;		/**		* 货币数目		*/		public int Money;		/**		* 原价格		*/		public int LastMoney;		/**		* 文本		*/		public string Text;		static public Dictionary<int, TbDataTimeLimit> temples=new Dictionary<int,TbDataTimeLimit>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataTimeLimit tp=new TbDataTimeLimit();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ItembaseID=(int)tb["ItembaseID"];				tp.ItemNum=(int)tb["ItemNum"];				tp.MoneyKind=(int)tb["MoneyKind"];				tp.Money=(int)tb["Money"];				tp.LastMoney=(int)tb["LastMoney"];				tp.Text=(string)tb["Text"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataTimeLimit select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}