using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataTitle{		/**		* ID		*/		public int Id;		/**		* 分组		*/		public int Team;		/**		* 最小物攻		*/		public int MinATK;		/**		* 最大物攻		*/		public int MaxATK;		/**		* 最小法攻		*/		public int MinMATK;		/**		* 最大法攻		*/		public int MaxMATK;		/**		* 最小道攻		*/		public int MinSATK;		/**		* 最大道攻		*/		public int MaxSATK;		/**		* 最小物防		*/		public int MinDEF;		/**		* 最大物防		*/		public int MaxDEF;		/**		* 最小法防		*/		public int MinMDEF;		/**		* 最大法防		*/		public int MaxMDEF;		/**		* 幸运		*/		public int Luck;		/**		* 血量上限		*/		public int MaxHP;		/**		* 魔力上限		*/		public int MaxMP;		/**		* 命中		*/		public int HIT;		/**		* 暴击率		*/		public int CRIRate;		/**		* 抗暴击		*/		public int DUCrate;		/**		* 伤害增加		*/		public int DMGrate;		/**		* 免伤		*/		public int DTR;		/**		* 名称		*/		public string Name;		/**		* 备注		*/		public string Desc;		/**		* 资源		*/		public string Icon;		static public Dictionary<int, TbDataTitle> temples=new Dictionary<int,TbDataTitle>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataTitle tp=new TbDataTitle();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Team=(int)tb["Team"];				tp.MinATK=(int)tb["MinATK"];				tp.MaxATK=(int)tb["MaxATK"];				tp.MinMATK=(int)tb["MinMATK"];				tp.MaxMATK=(int)tb["MaxMATK"];				tp.MinSATK=(int)tb["MinSATK"];				tp.MaxSATK=(int)tb["MaxSATK"];				tp.MinDEF=(int)tb["MinDEF"];				tp.MaxDEF=(int)tb["MaxDEF"];				tp.MinMDEF=(int)tb["MinMDEF"];				tp.MaxMDEF=(int)tb["MaxMDEF"];				tp.Luck=(int)tb["Luck"];				tp.MaxHP=(int)tb["MaxHP"];				tp.MaxMP=(int)tb["MaxMP"];				tp.HIT=(int)tb["HIT"];				tp.CRIRate=(int)tb["CRIRate"];				tp.DUCrate=(int)tb["DUCrate"];				tp.DMGrate=(int)tb["DMGrate"];				tp.DTR=(int)tb["DTR"];				tp.Name=(string)tb["Name"];				tp.Desc=(string)tb["Desc"];				tp.Icon=(string)tb["Icon"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataTitle select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}